insert  into `t_content`(`content_id`,`content_name`,`content_type`,`content`,`user_id`,`url`,`content_time`) values (21,'Visi dan Misi','Profil','<!--[if gte mso 9]><xml>\r\n <w:WordDocument>\r\n  <w:View>Normal</w:View>\r\n  <w:Zoom>0</w:Zoom>\r\n  <w:TrackMoves/>\r\n  <w:TrackFormatting/>\r\n  <w:PunctuationKerning/>\r\n  <w:ValidateAgainstSchemas/>\r\n  <w:SaveIfXMLInvalid>false</w:SaveIfXMLInvalid>\r\n  <w:IgnoreMixedContent>false</w:IgnoreMixedContent>\r\n  <w:AlwaysShowPlaceholderText>false</w:AlwaysShowPlaceholderText>\r\n  <w:DoNotPromoteQF/>\r\n  <w:LidThemeOther>EN-US</w:LidThemeOther>\r\n  <w:LidThemeAsian>X-NONE</w:LidThemeAsian>\r\n  <w:LidThemeComplexScript>X-NONE</w:LidThemeComplexScript>\r\n  <w:Compatibility>\r\n   <w:BreakWrappedTables/>\r\n   <w:SnapToGridInCell/>\r\n   <w:WrapTextWithPunct/>\r\n   <w:UseAsianBreakRules/>\r\n   <w:DontGrowAutofit/>\r\n   <w:SplitPgBreakAndParaMark/>\r\n   <w:EnableOpenTypeKerning/>\r\n   <w:DontFlipMirrorIndents/>\r\n   <w:OverrideTableStyleHps/>\r\n  </w:Compatibility>\r\n  <m:mathPr>\r\n   <m:mathFont m:val=\"Cambria Math\"/>\r\n   <m:brkBin m:val=\"before\"/>\r\n   <m:brkBinSub m:val=\"&#45;-\"/>\r\n   <m:smallFrac m:val=\"off\"/>\r\n   <m:dispDef/>\r\n   <m:lMargin m:val=\"0\"/>\r\n   <m:rMargin m:val=\"0\"/>\r\n   <m:defJc m:val=\"centerGroup\"/>\r\n   <m:wrapIndent m:val=\"1440\"/>\r\n   <m:intLim m:val=\"subSup\"/>\r\n   <m:naryLim m:val=\"undOvr\"/>\r\n  </m:mathPr></w:WordDocument>\r\n</xml><![endif]-->\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">Visi\r\nDinas Sosial Kota Bandung yaitu :</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><b><i style=\"mso-bidi-font-style:\r\nnormal\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">“Kesejahteraan\r\nSosial dari, oleh, dan untuk Masyarakat menuju Bandung yang Unggul, Nyaman, dan\r\nSejahtera”</span></i></b></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><b><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">&nbsp;</span></b></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;;\r\nmso-bidi-font-weight:bold\">Visi Dinas Sosial Kota Bandung tersebut dijabarkan\r\nsebagai berikut :</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;;\r\nmso-bidi-font-weight:bold\">&nbsp;</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><b><i style=\"mso-bidi-font-style:\r\nnormal\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">Kesejahteraan\r\nSosial</span></i></b><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;;\r\nmso-bidi-font-weight:bold\"> mengandung pengertian </span><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;;mso-bidi-font-family:Arial\">suatu\r\ntata kehidupan dan penghidupan sosial, materi maupun spiritual yang diliputi\r\noleh rasa keselamatan, kesusilaan dan ketentraman lahir batin, yang\r\nmemungkinkan bagi setiap warga negara untuk mengadakan usaha pemenuhan\r\nkebutuhan-kebutuhan jasmani, rohani dan sosial yang sebaik-baiknya bagi diri,\r\nkeluarga serta masyarakat dengan menjunjung tinggi hak-hak asasi serta\r\nkewajiban manusia sesuai Pancasila</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;;\r\nmso-bidi-font-family:Arial\">&nbsp;</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><b style=\"mso-bidi-font-weight:\r\nnormal\"><i style=\"mso-bidi-font-style:normal\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;;\r\nmso-bidi-font-family:Arial\">Dari Masyarakat</span></i></b><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;;mso-bidi-font-family:Arial\">\r\nmengandung pengertian bahwa sumber pembiayaan untuk penyelenggaraan\r\nkesejahteraan sosial yang dilaksanakan oleh Dinas Sosial Kota Bandung secara\r\ntidak langsung bersumber dari masyarakat melalui mekanisme APBD Kota Bandung .</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">&nbsp;</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><b style=\"mso-bidi-font-weight:\r\nnormal\"><i style=\"mso-bidi-font-style:normal\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">Oleh\r\nMasyarakat</span></i></b><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">\r\nmengandung pengertian bahwa Dinas Sosial Kota Bandung mengupayakan agar\r\nmasyarakat tidak hanya berperan sebagai objek penyelenggaraan kesejahteraan\r\nsosial akan tetapi juga dapat berfungsi sebagai subjek penyelenggaraan\r\nkesejahteraan sosial melalui Pembinaan Potensi dan Sumber Kesejahteraan Sosial\r\n(PSKS).</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">&nbsp;</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><b style=\"mso-bidi-font-weight:\r\nnormal\"><i style=\"mso-bidi-font-style:normal\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">Untuk\r\nMasyarakat</span></i></b><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">\r\nmengandung pengertian bahwa penyelenggaraan kesejahteraan sosial ditujukan\r\nsebesar-sebesarnya untuk kemakmuran masyarakat Kota Bandung.</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">&nbsp;</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><b style=\"mso-bidi-font-weight:\r\nnormal\"><i style=\"mso-bidi-font-style:normal\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">Unggul\r\n</span></i></b><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">mengandung\r\npengertian Kota Bandung sebagai kota yang terbaik dan terdepan sebagai contoh\r\nbagi daerah lain dalam upaya terobosan perubahan ke arah yang lebih baik</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">&nbsp;</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><b style=\"mso-bidi-font-weight:\r\nnormal\"><i style=\"mso-bidi-font-style:normal\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">Nyaman</span></i></b><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\"> mengandung pengertian sebagai\r\nupaya terciptanya suatu kondisi terpeliharanya kualitas lingkungan serta dapat\r\nmemberikan kesegaran dan kesejukan bagi warga kota dan dapat memberikan\r\npemenuhan atas kebutuhan dasar seperti tanah, air, dan udara.</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">&nbsp;</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><b style=\"mso-bidi-font-weight:\r\nnormal\"><i style=\"mso-bidi-font-style:normal\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">Sejahtera\r\n</span></i></b><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">mengandung\r\npengertian mengarahkan semua pembangunan kota pada pemenuhan lahir dan batin\r\nwarganya agar dapat menjalankan fungsi sosialnya.</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">&nbsp;</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"line-height:115%\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\">Untuk\r\nmencapai Visi tersebut, Dinas Sosial Kota Bandung merumuskan misi sebagai\r\nberikut :</span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpFirst\" style=\"margin-left:21.3pt;mso-add-space:\r\nauto;text-indent:-21.3pt;line-height:115%;mso-list:l0 level1 lfo1\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;;mso-fareast-font-family:&quot;Bookman Old Style&quot;;\r\nmso-bidi-font-family:&quot;Bookman Old Style&quot;\"><span style=\"mso-list:Ignore\">1.<span style=\"font:7.0pt &quot;Times New Roman&quot;\">&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;;mso-ansi-language:ES\" lang=\"ES\">Mewujudkan\r\nkesejahteraan sosial melalui peningkatan partisipasi sosial dan masyarakat,\r\ndimana terdapat peran aktif dari masyarakat dalam penanganan masalah\r\nkesejahteraan sosial </span><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\"></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"margin-left:21.3pt;mso-add-space:\r\nauto;text-indent:-21.3pt;line-height:115%;mso-list:l0 level1 lfo1\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;;mso-fareast-font-family:&quot;Bookman Old Style&quot;;\r\nmso-bidi-font-family:&quot;Bookman Old Style&quot;\"><span style=\"mso-list:Ignore\">2.<span style=\"font:7.0pt &quot;Times New Roman&quot;\">&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;;mso-ansi-language:ES\" lang=\"ES\">Mewujudkan\r\nkesejahteraan sosial melalui peningkatan rehabilitasi sosial guna memulihkan\r\nketidakberdayaan masayarakat dalam melaksanakan fungsi sosialnya</span><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\"> </span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"margin-left:21.3pt;mso-add-space:\r\nauto;text-indent:-21.3pt;line-height:115%;mso-list:l0 level1 lfo1\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;;mso-fareast-font-family:&quot;Bookman Old Style&quot;;\r\nmso-bidi-font-family:&quot;Bookman Old Style&quot;\"><span style=\"mso-list:Ignore\">3.<span style=\"font:7.0pt &quot;Times New Roman&quot;\">&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;;mso-ansi-language:ES\" lang=\"ES\">Mewujudkan\r\nkesejahteraan sosial melalui peningkatan pelayanan sosial, yang mengandung\r\npengertian optimalisasi pelayanan terhadap Penyandang Masalah Kesejahteraan\r\nSosial (PMKS)</span><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;\"> </span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpLast\" style=\"margin-left:21.3pt;mso-add-space:auto;\r\ntext-indent:-21.3pt;line-height:115%;mso-list:l0 level1 lfo1\"><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;;mso-fareast-font-family:&quot;Bookman Old Style&quot;;\r\nmso-bidi-font-family:&quot;Bookman Old Style&quot;\"><span style=\"mso-list:Ignore\">4.<span style=\"font:7.0pt &quot;Times New Roman&quot;\">&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span style=\"font-family:&quot;Bookman Old Style&quot;,&quot;serif&quot;;mso-ansi-language:ES\" lang=\"ES\">Mewujudkan\r\nkesejahteraan sosial melalui pembinaan terhadap rawan sosial keluarga dan anak,\r\nrawan sosial memiliki makna golongan masyarakat yang beresiko tinggi menjadi\r\nPenyandang Masalah Kesejahteraan Sosial (PMKS)</span><span style=\"font-family:\r\n&quot;Bookman Old Style&quot;,&quot;serif&quot;\"></span></p>\r\n\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n\r\n<!--[if gte mso 9]><xml>\r\n <w:LatentStyles DefLockedState=\"false\" DefUnhideWhenUsed=\"false\"\r\n  DefSemiHidden=\"false\" DefQFormat=\"false\" DefPriority=\"99\"\r\n  LatentStyleCount=\"371\">\r\n  <w:LsdException Locked=\"false\" Priority=\"0\" QFormat=\"true\" Name=\"Normal\"/>\r\n  <w:LsdException Locked=\"false\" Priority=\"9\" QFormat=